mapper.select:
SELECT id,username,start_time,end_time,departure_addr,destination_addr, reason,status FROM application
AND id = #{id}
AND departure_addr like CONCAT('%',#{departureAddr},'%')
AND destination_addr LIKE CONCAT('%',#{destinationAddr},'%')
AND status = #{status}

mapper.update:
UPDATE application


        id = #{id},


        status = #{status},


        update_time = #{updateTime},


        reject_reason = #{rejectReason}


WHERE id = #{id}
